export CFLAGS="$CFLAGS -fPIC"
export CXXFLAGS="$CXXFLAGS -fPIC"

NPROCESSORS=$( getconf _NPROCESSORS_ONLN )

mkdir deps/transmission/build
cd deps/transmission/build

cmake \
  -DCMAKE_BUILD_TYPE=Release \
  -DENABLE_DAEMON=OFF \
  -DENABLE_GTK=OFF \
  -DENABLE_QT=OFF \
  -DENABLE_MAC=OFF \
  -DREBUILD_WEB=OFF \
  -DINSTALL_WEB=OFF \
  -DENABLE_UTILS=OFF \
  -DENABLE_CLI=OFF \
  -DENABLE_TESTS=OFF \
  -DENABLE_UTP=ON \
  -DENABLE_NLS=OFF \
  -DINSTALL_DOC=OFF \
  -DINSTALL_LIB=OFF \
  -DUSE_SYSTEM_EVENT2=OFF \
  -DUSE_SYSTEM_DEFLATE=OFF \
  -DUSE_SYSTEM_DHT=OFF \
  -DUSE_SYSTEM_MINIUPNPC=OFF \
  -DUSE_SYSTEM_NATPMP=OFF \
  -DUSE_SYSTEM_UTP=OFF \
  -DUSE_SYSTEM_B64=OFF \
  -DUSE_SYSTEM_PSL=OFF \
  -DWITH_CRYPTO=openssl \
  ..

make -j $NPROCESSORS
cd ../../..
